window.addEventListener('load', ()=>{var objMain=document.getElementById('wrapper'),hideMainMenu='hide_main_menu';document.getElementById('collapser').addEventListener('mouseover', (e)=>{var t=setTimeout(()=>{e.preventDefault();if(objMain.classList.contains('hide-mm')) {objMain.classList.remove('hide-mm');dotclear.dropLocalData(hideMainMenu);document.querySelector('input#qx')?.focus();return;}objMain.classList.add('hide-mm');dotclear.storeLocalData(hideMainMenu, !0);document.querySelector('#content a.go_home')?.focus();}, 500);e.target.addEventListener('mouseleave', ()=>{clearTimeout(t);});});});
